function loadImage(a) {
			$("#product-photo").addClass('showLoader');

			var image_src = a.attr('href');

    	var img = new Image();

    	img = $(img);

    	img.hide();
    	img.attr('src', image_src);

    	$("#product-photo").html(img);

    	img.load(function() {
    		$("#product-photo").removeClass('showLoader');
       	$(this).stop().fadeIn(500);
       	$(".view-product-photos a.thumb").removeClass('active');
       	a.addClass('active');
    	});

}

function loadSidebar(a) {
  // Extract node ID.
  var nid = $(a).children('span').html();

  /*
  $.get('/jsonnode/node/' + nid + '/json', function(data) {
    loadType(data);
  });
  */
  $.ajax({
    type: 'GET',
    url: '/jsonnode/node/' + nid + '/json',
    dataType: 'json',
    success: function (data) {
      loadType(data);
    }
  });
}

function loadType(node) {
  var block = $('#block-block-58');

  var title = node.type + '<br />' + node.category;

  $('img', block).attr('src', node.photo);
  $('.pictured-product-title a', block).html(title);
  $('.pictured-product a', block).attr('href', node.link);
}

